import { Link, useLocation } from 'react-router-dom';
import { FaLinkedin, FaGithub, FaWhatsapp } from 'react-icons/fa';

export default function NavBar({ navBarOptions }) {
  let location = useLocation();

  return (
    <nav className="navbar navbar-expand-lg bg-secondary sticky-top">
      <div className="container-fluid">
        <span className="navbar-brand barra px-3"><strong>Portafolio</strong></span>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
        >
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className="collapse navbar-collapse justify-content-between" id="navbarNav">
          {/* Opciones de la izquierda */}
          <ul className="navbar-nav">
            {navBarOptions.map((navBarOption, index) => {
              let isActive = navBarOption.to === location.pathname ? 'active' : '';
              return (
                <li className="nav-item" key={index}>
                  <Link className={`nav-link barra ${isActive}`} to={navBarOption.to}>
                    {navBarOption.option}
                  </Link>
                </li>
              );
            })}
          </ul>

          {/* √çconos de redes sociales a la derecha */}
          <div className="social-icons">
            <a href="https://www.linkedin.com/" target="_blank" rel="noopener noreferrer">
              <FaLinkedin className="social-icon" />
            </a>
            <a href="https://github.com/" target="_blank" rel="noopener noreferrer">
              <FaGithub className="social-icon" />
            </a>
            <a href="https://wa.me/" target="_blank" rel="noopener noreferrer">
              <FaWhatsapp className="social-icon" />
            </a>
          </div>
        </div>
      </div>
    </nav>
  );
}
