import { Link, useLocation } from 'react-router-dom';

export default function NavBar({ navBarOptions }) {
  let location = useLocation();
  return (
    <nav className="navbar navbar-expand-lg bg-secondary sticky-top">
      <div className="container-fluid">
        <span className="navbar-brand barra px-3"><strong>Portafolio</strong></span>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav">
            {navBarOptions.map((navBarOption, index) => {
              let isActive = '';
              if (navBarOption.to === location.pathname) {
                isActive = 'active';
              }
              return (
                <li className="nav-item" key={index}>
                  <Link className={'nav-link barra ' + isActive} to={navBarOption.to}>
                    {navBarOption.option}
                  </Link>
                </li>
              );
            })}
          </ul>
        </div>
      </div>

      <div className="col-sm-3">
        
         <a href="https://wa.me/62423144"><i class="bi bi-whatsapp px-3 icono"></i></a> 
        <a href="https://github.com/ElianaRocio"><i className="bi bi-github px-3 icono" ></i></a>
        
      </div>
    </nav>
  );
}
