import img21 from '../../assets/images/img21.png'; 
import img22 from '../../assets/images/img22.png'; 
import img23 from '../../assets/images/img23.png'; 
import img24 from '../../assets/images/img24.png'; 
import img25 from '../../assets/images/img25.png'; 
import img26 from '../../assets/images/img26.png'; 
import img27 from '../../assets/images/img27.png'; 
 
import { Link} from 'react-router-dom';
import Descripcion from "../../components/Descripcion/Descripcion";


let lugar = [
    {        
        tiempo: 'Bot de Whatsapp ',     
        costo:   'Disclaimer: Para mantener la confidencialidad se modificaron datos de la empresa original',  
        precio: 'Cargo: UX Design Researcher',
        texto: 'Datos: Este proyecto se trabajo en un equipo de 5 personas, en un tiempo de 1 semana',
    },
];

export default function Botinfo() {
    return (
        <div>
            <div className="container my-3">
            <h1 className="titulo2">UX Writting</h1>
                <div className="col-sm">
                    <div className="lugar">
                        {lugar.map((descripcion, index) => {
                            return (
                                <Descripcion
                                    key={index}                                                                      texto={descripcion.texto}
                                    tiempo={descripcion.tiempo}
                                    precio={descripcion.precio}
                                    costo={descripcion.costo}
                                >
                                </Descripcion>
                            );
                        })}
                    </div>                    
                </div>

               
                <div className='container my-3'>
                    <div className='row'>
                    <div className='col-sm'>
                       
                       <p className='titulo0'>Objetivo</p>
                       <p className='texto1'>Se realizó una evaluación de UX Writing para un bot de WhatsApp diseñado para facilitar el acceso a préstamos bancarios. Para ello se hizo uso de una prueba de usabilidad a 5 personas, en la cuál se identificó problemas significativos en la comprensión de los textos del bot. </p>
                      <p className='titulo0'>Metodología</p>
                       <p className='texto1'>Para ello se hizo uso de una prueba de usabilidad a 5 personas, en la cuál se identificó problemas significativos en la comprensión de los textos del bot.</p>
                        <p className='titulo0'>Resulta</p>
                       <p className='texto1'></p>
                        <p className='titulo0'></p>
                       <p className='texto1'></p>
                     
                     </div>                     
                    </div>
                </div>
                <div className='container my-3'>
                
                    <div className='row'>                     
                    <div className='col-sm'>            
                        </div>
                     <div className='col-sm'>            
                                             
                    </div>
                    </div>
                </div>      

               </div>     


                    
            <div className="container my-3">
                <div className="row">
                    <div className="col-sm my-3" style={{ textAlign: 'right' }}>
                        <Link to="/">   <button className="btn btn-primary">Home</button></Link>
                    </div>
                    <div className="col-sm my-3">
                        <Link to="/UX/UI">   <button className="btn btn-primary">Siguiente</button></Link>
                       
                    </div>
                </div>
            </div>
             
          
            
        </div>
    );

}
