import { Link, useLocation } from 'react-router-dom';
import { BsLinkedin, BsGithub, BsWhatsapp } from 'react-icons/bs';

export default function NavBar({ navBarOptions }) {
  let location = useLocation();

  // Estilos en línea para el Navbar
  const navBarStyle = {
    backgroundColor: '#6c757d', // Color de fondo del Navbar
    padding: '0.5rem 1rem', // Espaciado ligero
    position: 'static', // Posición estática
    zIndex: 999, // Asegura que esté por encima de otros elementos
  };

  const navLinkStyle = {
    padding: '0.5rem 1rem',
    color: '#ffffff', // Color del texto
    transition: 'color 0.3s ease',
  };

  const navLinkHoverStyle = {
    color: '#007bff', // Color de hover
  };

  const iconStyle = {
    fontSize: '1.25rem', // Tamaño del icono
    color: '#ffffff', // Color del icono
    marginLeft: '0.5rem',
    marginRight: '0.5rem',
    transition: 'color 0.3s ease',
  };

  const iconHoverStyle = {
    color: '#007bff', // Color de hover para los iconos
  };

  return (
    <nav className="navbar navbar-expand-lg" style={navBarStyle}>
      <div className="container-fluid">
        <span className="navbar-brand" style={{ fontSize: '1.25rem', fontWeight: 'bold' }}>Portafolio</span>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
        >
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className="collapse navbar-collapse d-flex justify-content-between" id="navbarNav">
          {/* Opciones de la izquierda */}
          <ul className="navbar-nav me-auto">
            {navBarOptions.map((navBarOption, index) => {
              let isActive = navBarOption.to === location.pathname ? 'active' : '';
              return (
                <li className="nav-item" key={index}>
                  <Link 
                    className={`nav-link ${isActive}`} 
                    to={navBarOption.to} 
                    style={navLinkStyle}
                    onMouseOver={(e) => e.currentTarget.style.color = navLinkHoverStyle.color}
                    onMouseOut={(e) => e.currentTarget.style.color = ''}
                  >
                    {navBarOption.option}
                  </Link>
                </li>
              );
            })}
          </ul>

          {/* Íconos de redes sociales a la derecha */}
          <div className="d-flex align-items-center">
            <a 
              href="https://wa.me/62423144" 
              target="_blank" 
              rel="noopener noreferrer"
              style={iconStyle}
              onMouseOver={(e) => e.currentTarget.style.color = iconHoverStyle.color}
              onMouseOut={(e) => e.currentTarget.style.color = ''}
            >
              <BsWhatsapp />
            </a>
            <a 
              href="https://github.com/ElianaRocio" 
              target="_blank" 
              rel="noopener noreferrer"
              style={iconStyle}
              onMouseOver={(e) => e.currentTarget.style.color = iconHoverStyle.color}
              onMouseOut={(e) => e.currentTarget.style.color = ''}
            >
              <BsGithub />
            </a>
            <a 
              href="https://www.linkedin.com/in/elianamdb/" 
              target="_blank" 
              rel="noopener noreferrer"
              style={iconStyle}
              onMouseOver={(e) => e.currentTarget.style.color = iconHoverStyle.color}
              onMouseOut={(e) => e.currentTarget.style.color = ''}
            >
              <BsLinkedin />
            </a>
          </div>
        </div>
      </div>
    </nav>
  );
}
