import { Link, useLocation } from 'react-router-dom';

export default function NavBar({ navBarOptions }) {
  let location = useLocation();
  return (
    <nav className="navbar navbar-expand-lg sticky-top" style={{ backgroundColor: '#ffffff' }}>
      <div className="container-fluid">
        <span className="navbar-brand" style={{ color: '#0a4c58', fontFamily: "'Quicksand', sans-serif", fontWeight: 'bolder' }}>
          <strong>Portafolio</strong>
        </span>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav">
            {navBarOptions.map((navBarOption, index) => {
              let isActive = '';
              if (navBarOption.to === location.pathname) {
                isActive = 'active';
              }
              return (
                <li className="nav-item" key={index}>
                  <Link
                    className={'nav-link' + (isActive ? ' active' : '')}
                    to={navBarOption.to}
                    style={{
                      color: '#0a4c58', // Texto en tono m치s oscuro que #0c6980
                      fontFamily: "'Quicksand', sans-serif",
                      fontWeight: 'bolder',
                      borderRadius: '20px',
                      padding: '10px 15px',
                      textAlign: 'center',
                      textDecoration: 'none',
                      transition: 'background-color 0.3s ease, color 0.3s ease',
                      display: 'block'
                    }}
                    onMouseEnter={e => {
                      e.currentTarget.style.backgroundColor = '#0c6980'; // Color de fondo en hover
                      e.currentTarget.style.color = '#ffffff'; // Texto blanco en hover
                    }}
                    onMouseLeave={e => {
                      e.currentTarget.style.backgroundColor = 'transparent'; // Fondo transparente
                      e.currentTarget.style.color = '#0a4c58'; // Texto en tono m치s oscuro
                    }}
                  >
                    {navBarOption.option}
                  </Link>
                </li>
              );
            })}
          </ul>
        </div>
        <div className="d-flex">
          <a href="https://www.linkedin.com/in/elianamdb/" style={{ color: '#0a4c58', fontSize: '20px', margin: '0 10px' }}>
            <i className="bi bi-linkedin"></i>
          </a>
          <a href="https://github.com/ElianaRocio" style={{ color: '#0a4c58', fontSize: '20px', margin: '0 10px' }}>
            <i className="bi bi-github"></i>
          </a>
          <a href="https://wa.me/62423144" style={{ color: '#0a4c58', fontSize: '20px', margin: '0 10px' }}>
            <i className="bi bi-whatsapp"></i>
          </a>
        </div>
      </div>
    </nav>
  );
}
