import img02 from '../../assets/images/img02.png'; 
import img03 from '../../assets/images/img03.png'; 
import img04 from '../../assets/images/img04.png'; 
import img05 from '../../assets/images/img05.png'; 
import img06 from '../../assets/images/img06.png'; 
import img07 from '../../assets/images/img07.png'; 
import img08 from '../../assets/images/img08.png'; 
import img09 from '../../assets/images/img09.png'; 
import img00 from '../../assets/images/img00.png';  
import img11 from '../../assets/images/img11.png';  
import { Link} from 'react-router-dom';
import Descripcion from "../../components/Descripcion/Descripcion";


let lugar = [
    {
        texto: 'Construcción de un prototitpo de alta fidelidad para registrar datos de micro empresas, el cuál fue sometido a una evaluación experta en la cuál se obtuvieron puntos de mejora y hallazgos con los cuáles se realizó un rediseño de la plataforma para evitar el abandono de la misma y la frustración por parte de los usuarios al no poder realizar las tareas.',
        tiempo: 'Rediseño de plataforma',        
    },
];

export default function Hboinfo() {
    return (
        <div>
            <div className="container my-3">
            <h1 className="titulo2">Formulario de registro</h1>
                <div className="col-sm">
                    <div className="lugar">
                        {lugar.map((descripcion, index) => {
                            return (
                                <Descripcion
                                    key={index}                                                                      texto={descripcion.texto}
                                    tiempo={descripcion.tiempo}
                                    precio={descripcion.precio}
                                    costo={descripcion.costo}
                                >
                                </Descripcion>
                            );
                        })}
                    </div>                    
                </div>

               
                <div className='container my-3'>
                    <div className='row'>
                    <div className='col-sm'>
                       <p className='texto1'>Para un mejor entendimiento del diseño de una app de streaming se hizo un caso de estudio con la finalidad de reconstruirla partiendo del sistema de diseño, la creación de componentes y el armado de pantallas.</p>
                     <p className='my-3 titulo1'>uía de estilo</p>
                     </div>
                    </div>
                </div>


 < div className='container my-3'>
                    <div className='row'>
                    <div className='col-sm'>
                        <p className='titulo0'></p>
                        <p className='texto1'></p>
                        <div className='text-center'>
                        <img src={img03} alt="img" className="img-fluid" width="500"/> 
                        </div>
                    </div>
                    </div>
                </div>
                

                 <div className='container my-3'>
                    <div className='row'>
                    <div className='col-sm'>
                        <p className='titulo0'></p>
                        <p className='texto1'></p>
                        <div className='text-center'>
                        <img src={img03} alt="img" className="img-fluid" width="500"/> 
                        </div>
                    </div>
                    </div>
                </div>

            </div>     
                    
            <div className="container my-3">
                <div className="row">
                    <div className="col-sm my-3" style={{ textAlign: 'right' }}>
                        <Link to="/">   <button className="btn btn-primary">Home</button></Link>
                    </div>
                    <div className="col-sm my-3">
                        <Link to="/UX/UI">   <button className="btn btn-primary">Siguiente</button></Link>
                       
                    </div>
                </div>
            </div>
             
          
            
        </div>
    );

}
