import Table from "../../components/Table/Table";
import Disney1 from '../../assets/images/Disney1.png';
import Disney2 from '../../assets/images/Disney2.png';
import Disney3 from '../../assets/images/Disney3.png';
import Detalles from "../../components/Detalles/Detalles";
import Detalles2 from "../../components/Detalles2/Detalles2";
import Descripcion from "../../components/Descripcion/Descripcion";

let lugar = [
    {
        texto: 'La evaluación comparativa se llevó a cabo con el objetivo de identificar los aciertos y puntos de mejora de los planes de lealtad de dos farmacias. Para realizar la evaluacion experta se analizaron sus aplicaciones móviles, para ello se tomó en cuenta aspectos como el valor percibido, servicio al cliente, proceso de compra y facilidad de uso.',
        tiempo: 'Caso de estudio',
        
    },
];

export default function Info1Page() {
    return (
        <div>
            <div className="container my-3">
            <h1 className="titulo2">Evaluación comparativa</h1>
                <div className="col-sm">
                    <div className="lugar">
                        {lugar.map((descripcion, index) => {
                            return (
                                <Descripcion
                                    key={index}                                                                      texto={descripcion.texto}
                                    tiempo={descripcion.tiempo}
                                    precio={descripcion.precio}
                                    costo={descripcion.costo}
                                >
                                </Descripcion>
                            );
                        })}
                    </div>                    
                </div>

                <div className="row my-3">
                    <div className="col-sm my-3">
                    <img src={Disney1} alt="Disney1" className="img-fluid" width="300"/>
                    </div>
                    <div className="col-sm my-3">
                    <img src={Disney2} alt="Disney2" className="img-fluid" width="300"/>
                    </div>
                    <div className="col-sm my-3">
                    <img src={Disney3} alt="Disney3" className="img-fluid" width="300"/>
                    </div>
                </div>

                <div className="row my-3">
                    <h2 className="titulo3">Objetivo de investigacion</h2>
                    <p className="texto1">Como punto de partida se determinaron objetivos de las farmacias al incluir los planes de lealtdad en su aplicación móvil y los objetivos de investigación, los cuáles son: </p>
                    
                        <ul className="texto1">
                           <li>Identificar obstáculos y beneficios que tienen el plan de lealtad.</li>
                           <li>Encontrar las áreas de oportunidad de la aplicación durante y después del proceso de compra. </li>
                           <li>Descubrir las complicaciones de comunicación que tiene el cliente durante el proceso de compra y uso del plan de lealtad.</li>
                        </ul>
                    
                </div>

                <div className="row my-3">
                    <h2 className="titulo3">Metología y proceso</h2>
                    <ol className="texto1">
                           <li >Identificar obstáculos y beneficios que tienen el plan de lealtad.</li>
                           <div className="row"></div>
                           <li>2. Identificación de la dificultad de los pasos de compra por la app móvil, así como la motivación de los usuarios al usar los planes de lealtad, para ello se tomaron en cuenta 3 momentos clave y criterios de comparación. </li>
                           <li>3. A partir de ello se determinaron los hallazgos que se clasificaron como Aciertos ( buenas prácticas dentro del proceso de interacción ) y puntos de mejora (Malas prácticas que afectan negativamente al usuario). Ambos  se evaluaron con un nivel de severidad y se acompañan de buenas prácticas de otros productos digitales. </li>
                        </ol>
                    

                    <div className="row my-3">
                    
                    <Table />
</div>
                </div>
            </div>

            <div className="container my-3">
                <h3 className="titulo3">OTROS DETALLES</h3>
                <div className="row my-3">
                    <div className="col-sm my-3">
                        <Detalles />
                    </div>
                    <div className="col-sm my-3">
                        <Detalles />
                    </div>
                </div>

                <div className="row">
                    <div className="col-sm my-3">
                    <h3 className="titulo3">OTROS DETALLES</h3>
                    
                    <Detalles2 />
                    </div>
                    
                </div>
            </div>
            
        </div>
    );

}
