import img01 from '../../assets/images/img01.png'; 
import { Link} from 'react-router-dom';
import Descripcion from "../../components/Descripcion/Descripcion";


let lugar = [
    {
        texto: 'Construcción de un prototitpo de alta fidelidad para registrar datos de micro empresas, el cuál fue sometido a una evaluación experta en la cuál se obtuvieron puntos de mejora y hallazgos con los cuáles se realizó un rediseño de la plataforma para evitar el abandono de la misma y la frustración por parte de los usuarios al no poder realizar las tareas.',
        tiempo: 'Rediseño de plataforma',        
    },
];

export default function Formularioinfo() {
    return (
        <div>
            <div className="container my-3">
            <h1 className="titulo2">Formulario de registro</h1>
                <div className="col-sm">
                    <div className="lugar">
                        {lugar.map((descripcion, index) => {
                            return (
                                <Descripcion
                                    key={index}                                                                      texto={descripcion.texto}
                                    tiempo={descripcion.tiempo}
                                    precio={descripcion.precio}
                                    costo={descripcion.costo}
                                >
                                </Descripcion>
                            );
                        })}
                    </div>                    
                </div>

               
                <div className="row my-3">
                    <p className="texto1">Los objetivos que se tomaron en cuenta al momento de realizar la recarga, fueron:</p>
                    <ul className="texto1 mx-3">
                        <li>Evaluar la percepción de los usuarios.</li>
                        <li>Determinar las emociones que experimentan los usuarios.</li>
                        <li>Analizar la comprensión del contenido mostrado en las pantallas y evaluar si la cantidad de información es adecuada y comprensible.</li>
                        <li>Identificar en que contextos los usuarios usan el servicio</li>
                        <li>Evaluar su facilidad de uso y sus posibles dificultades de navegación en el flujo.</li>
                        <li>Analizar la satisfacción de los usuarios con cada pantalla presentada.</li>
                        
                    </ul>
                    
                    <div className="row my-3">                   
                    <h2 className="titulo1">Metodología</h2>
                    <p className="texto1">Se realizó una prueba de usabilidad virtual en base a un protocolo preparado con las preguntas elaboradas según los objetivos propuestos, para ello se precisó de 5 personas usuarias entre 18 a 25 años que utilicen la opción de recarga por medio de una app de banco. Posteriormente se consolidó la información a través de material audiovisual y escrito, por medio de notas registradas acerca de las opiniones y quotes de los usuarios, lo que permitió realizar el proceso de análisis.  </p>  
                    <div className="row text-center">
                    <img src={img01} alt="Notas usuarios" className="img-fluid" width="100"/>
                    </div>
                    
                    </div>                                   
                </div>

            </div>
       




          
                    
    





            <div className="container my-3">
                <div className="row">
                    <div className="col-sm my-3">
                        <Link to="/">   <button className="btn btn-primary"> Volver al menú</button></Link>
                    </div>
                    <div className="col-sm my-3">
                        <Link to="/UX/UI">   <button className="btn btn-primary"> Siguiente</button></Link>
                       
                    </div>
                </div>
            </div>
             
          
            
        </div>
    );

}
