import { Link, useLocation } from 'react-router-dom';

export default function NavBar({ navBarOptions }) {
  let location = useLocation();

  const navbarStyle = {
    backgroundColor: 'transparent',
    boxShadow: 'none',
    padding: '0.5rem 1rem',
    position: 'sticky',
    top: 0,
    width: '100%',
    zIndex: 1000
  };

  const navLinkStyle = {
    color: 'white',
    padding: '10px 20px',
    borderRadius: '20px',
    fontFamily: 'Quicksand, sans-serif',
    fontWeight: 'bolder',
    transition: 'background-color 0.3s, color 0.3s',
    textDecoration: 'none'
  };

  const navLinkHoverStyle = {
    backgroundColor: 'rgba(255, 255, 255, 0.2)',
    color: '#66b2ff'
  };

  const navLinkActiveStyle = {
    backgroundColor: '#66b2ff',
    color: 'white'
  };

  const navbarIconsStyle = {
    display: 'flex',
    alignItems: 'center',
    marginLeft: 'auto'
  };

  const iconStyle = {
    color: 'white',
    fontSize: '1.5rem',
    marginLeft: '1rem',
    textDecoration: 'none'
  };

  return (
    <nav className="navbar navbar-expand-lg" style={navbarStyle}>
      <div className="container-fluid">
        <span className="navbar-brand" style={{ color: '#66b2ff', fontFamily: 'Quicksand, sans-serif' }}>
          <strong>Portafolio</strong>
        </span>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav mr-auto">
            {navBarOptions.map((navBarOption, index) => {
              let isActive = location.pathname === navBarOption.to ? navLinkActiveStyle : {};
              return (
                <li className="nav-item" key={index}>
                  <Link
                    className="nav-link"
                    to={navBarOption.to}
                    style={{
                      ...navLinkStyle,
                      ...(location.pathname === navBarOption.to ? isActive : {}),
                      ':hover': navLinkHoverStyle
                    }}
                  >
                    {navBarOption.option}
                  </Link>
                </li>
              );
            })}
          </ul>
          <div className="navbar-icons" style={navbarIconsStyle}>
            <a href="https://www.linkedin.com/in/elianamdb/" style={iconStyle}><i className="bi bi-linkedin"></i></a>
            <a href="https://github.com/ElianaRocio" style={iconStyle}><i className="bi bi-github"></i></a>
            <a href="https://wa.me/62423144" style={iconStyle}><i className="bi bi-whatsapp"></i></a>
          </div>
        </div>
      </div>
    </nav>
  );
}
