import { Link, useLocation } from 'react-router-dom';

export default function NavBar({ navBarOptions }) {
  let location = useLocation();
  const activePath = location.pathname;

  return (
    <nav
      className="navbar navbar-expand-lg sticky-top"
      style={{
        backgroundColor: '#ffffff', // Fondo blanco del navbar
        borderBottom: '1px solid #ef3a5d', // Línea rosa más delgada
        padding: '0 1rem', // Espaciado horizontal
        marginBottom: '20px', // Espacio adicional debajo de la línea
      }}
    >
      <div className="container-fluid">
        <span
          className="navbar-brand"
          style={{
            color: '#0a4c58',
            fontFamily: "'Quicksand', sans-serif",
            fontWeight: 'bolder',
          }}
        >
          <strong>Portafolio</strong>
        </span>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav">
            {navBarOptions.map((navBarOption, index) => {
              const isActive = navBarOption.to === activePath;
              return (
                <li className="nav-item" key={index}>
                  <Link
                    className={`nav-link ${isActive ? 'active' : ''}`}
                    to={navBarOption.to}
                    style={{
                      color: isActive ? '#ffffff' : '#0a4c58', // Texto para la opción activa
                      backgroundColor: isActive ? '#ef3a5d' : 'transparent', // Fondo para la opción activa
                      fontFamily: "'Quicksand', sans-serif",
                      fontWeight: 'bolder',
                      borderRadius: '20px',
                      padding: '10px 15px',
                      textAlign: 'center',
                      textDecoration: 'none',
                      transition: 'background-color 0.3s ease, color 0.3s ease',
                      display: 'block',
                      borderBottom: `2px solid ${isActive ? '#ef3a5d' : 'transparent'}`, // Línea rosa para la opción activa
                      marginBottom: '5px', // Espacio adicional entre la línea y los botones
                    }}
                    onMouseEnter={(e) => {
                      if (!isActive) {
                        e.currentTarget.style.backgroundColor = '#0c6980'; // Color de fondo en hover
                        e.currentTarget.style.color = '#ffffff'; // Texto blanco en hover
                      }
                    }}
                    onMouseLeave={(e) => {
                      if (!isActive) {
                        e.currentTarget.style.backgroundColor = 'transparent'; // Fondo transparente
                        e.currentTarget.style.color = '#0a4c58'; // Texto en tono más oscuro
                      }
                    }}
                  >
                    {navBarOption.option}
                  </Link>
                </li>
              );
            })}
          </ul>
        </div>
        <div className="d-flex">
          <a
            href="https://www.linkedin.com/in/elianamdb/"
            style={{
              color: '#0a4c58',
              fontSize: '20px',
              margin: '0 10px',
            }}
          >
            <i className="bi bi-linkedin"></i>
          </a>
          <a
            href="https://github.com/ElianaRocio"
            style={{
              color: '#0a4c58',
              fontSize: '20px',
              margin: '0 10px',
            }}
          >
            <i className="bi bi-github"></i>
          </a>
          <a
            href="https://wa.me/62423144"
            style={{
              color: '#0a4c58',
              fontSize: '20px',
              margin: '0 10px',
            }}
          >
            <i className="bi bi-whatsapp"></i>
          </a>
        </div>
      </div>
    </nav>
  );
}
